    private void proseluasActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_proseluasActionPerformed
        // TODO add your handling code here:
        String teksalas = inalas.getText();
        double inputalas = Double.parseDouble(teksalas);
        String tekstinggi = intinggi.getText();
        double inputtinggi = Double.parseDouble(tekstinggi);
        double hasilluas = inputalas * inputtinggi ;
        String outputluas = String.valueOf(hasilluas);
        outluas.setText(outputluas);
    }//GEN-LAST:event_proseluasActionPerformed

    private void proseskelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_proseskelActionPerformed
        // TODO add your handling code here:
        String teksalas = inalas.getText();
        double inputalas = Double.parseDouble(teksalas);
        String tekssimir = inmiring.getText();
        double inputsimir = Double.parseDouble(tekssimir);
        double hasilkel = 2 * (inputalas + inputsimir);
        String outputkel = String.valueOf(hasilkel);
        outkel.setText(outputkel);
    }//GEN-LAST:event_proseskelActionPerformed

